            animation: successSlideUp 0.5s cubic-bezier(0.34, 1.56, 0.64, 1);
            box-shadow: 0 30px 90px rgba(0, 0, 0, 0.5);
        }

        @keyframes successSlideUp {
            from {
                opacity: 0;
                transform: translateY(50px) scale(0.9);
            }
            to {
                opacity: 1;
                transform: translateY(0) scale(1);
            }
        }

        .success-animation {
