        /* Print Styles */
        @media print {
            body * {
                visibility: hidden;
            }
            
            .transcript-modal,
            .transcript-modal * {
                visibility: visible;
            }
            
            .transcript-modal {
                position: absolute;
                left: 0;
                top: 0;
                width: 100%;
                height: auto;
                background: white;
            }
            
            .transcript-content {
                box-shadow: none;
                max-height: none;
                border-radius: 0;
            }
            
            .transcript-close,
            .transcript-footer {
                display: none !important;
            }
            
            .transcript-body {
                padding: 20px;
                overflow: visible;
            }
            
            .transcript-semester {
                page-break-inside: avoid;
            }
        }

