            font-size: 11px;
            font-weight: 600;
            border-radius: 20px;
            text-transform: uppercase;
            letter-spacing: 0.5px;
        }

        .course-status-badge.active {
            background: linear-gradient(135deg, #43e97b 0%, #38f9d7 100%);
            color: white;
            box-shadow: 0 4px 12px rgba(67, 233, 123, 0.3);
        }

        .course-status-badge.completed {
            background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
            color: white;
            box-shadow: 0 4px 12px rgba(102, 126, 234, 0.3);
        }

        .course-info p {
            font-size: 14px;
            color: #7f8c8d;
            display: flex;
            align-items: center;
            gap: 12px;
            flex-wrap: wrap;
        }

        .course-info p i {
            font-size: 12px;
            color: #95a5a6;
        }

        .course-progress {
            display: flex;
            align-items: center;
            justify-content: space-between;
            padding: 16px;
            background: linear-gradient(135deg, rgba(102, 126, 234, 0.05) 0%, rgba(118, 75, 162, 0.05) 100%);
            border-radius: 12px;
            margin-top: 12px;
        }

        .progress-info {
            display: flex;
            flex-direction: column;
            gap: 4px;
        }

        .progress-label {
            font-size: 12px;
            color: #7f8c8d;
            font-weight: 600;
            text-transform: uppercase;
            letter-spacing: 0.5px;
        }

        .progress-percent {
            font-size: 24px;
            font-weight: 700;
            background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
            -webkit-background-clip: text;
            -webkit-text-fill-color: transparent;
            background-clip: text;
        }

        .progress-bar-wrapper {
            position: relative;
            flex: 1;
            margin-left: 20px;
        }

        .progress-bar {
            width: 100%;
            height: 8px;
            background: rgba(102, 126, 234, 0.1);
            border-radius: 10px;
            overflow: visible;
            position: relative;
        }

        .progress-fill {
            height: 100%;
            background: linear-gradient(90deg, #667eea 0%, #764ba2 100%);
            border-radius: 10px;
            transition: width 0.6s ease;
            position: relative;
            box-shadow: 0 2px 8px rgba(102, 126, 234, 0.4);
        }

        .progress-fill::after {
            content: '';
            position: absolute;
            right: -4px;
            top: 50%;
            transform: translateY(-50%);
            width: 16px;
            height: 16px;
            background: white;
            border: 3px solid #667eea;
            border-radius: 50%;
            box-shadow: 0 2px 8px rgba(102, 126, 234, 0.3);
        }

        .course-item:hover .progress-fill {
            background: linear-gradient(90deg, #764ba2 0%, #667eea 100%);
        }

        .course-meta {
            display: flex;
            align-items: center;
            gap: 16px;
            padding-top: 12px;
            border-top: 1px solid rgba(0, 0, 0, 0.05);
            margin-top: 12px;
        }

        .course-meta-item {
            display: flex;
            align-items: center;
            gap: 6px;
            font-size: 13px;
            color: #7f8c8d;
        }

        .course-meta-item i {
            font-size: 14px;
            color: #667eea;
        }

        .course-actions {
            display: flex;
            gap: 8px;
            margin-top: 16px;
        }

        .course-action-btn {
            flex: 1;
            padding: 10px 16px;
            border: none;
            border-radius: 10px;
            font-size: 14px;
            font-weight: 600;
            cursor: pointer;
            transition: all 0.3s ease;
            display: flex;
            align-items: center;
            justify-content: center;
            gap: 8px;
        }

        .course-action-btn.primary {
            background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
            color: white;
            box-shadow: 0 4px 12px rgba(102, 126, 234, 0.3);
        }

        .course-action-btn.primary:hover {
            transform: translateY(-2px);
            box-shadow: 0 6px 20px rgba(102, 126, 234, 0.4);
        }

        .course-action-btn.secondary {
            background: white;
            color: #667eea;
            border: 2px solid #667eea;
        }

        .course-action-btn.secondary:hover {
            background: #667eea;
            color: white;
        }

        
        .event-list {
            display: flex;
            flex-direction: column;
            gap: 15px;
        }

        .event-item {
            padding: 15px;
            border-left: 4px solid #667eea;
            background: #f8f9ff;
            border-radius: 8px;
            transition: all 0.3s ease;
        }

        .event-item:hover {
            transform: translateX(5px);
        }

        .event-date {
            display: inline-block;
            padding: 4px 10px;
            background: #667eea;
            color: white;
            border-radius: 5px;
            font-size: 12px;
            font-weight: 600;
            margin-bottom: 8px;
        }

        .event-item h4 {
            font-size: 15px;
            color: #2c3e50;
            margin-bottom: 5px;
        }

        .event-item p {
            font-size: 13px;
            color: #7f8c8d;
        }

        
        .assignments-table {
            width: 100%;
            border-collapse: collapse;
        }

        .assignments-table th {
            text-align: left;
            padding: 15px;
            background: #f8f9fa;
            color: #2c3e50;
            font-weight: 600;
            font-size: 14px;
        }

        .assignments-table td {
            padding: 15px;
            border-bottom: 1px solid #e0e6ed;
            font-size: 14px;
        }

        .assignments-table tr:hover {
            background: #f8f9fa;
        }

        .status-badge {
            display: inline-block;
            padding: 5px 12px;
            border-radius: 20px;
            font-size: 12px;
            font-weight: 600;
        }

        .status-badge.pending {
            background: #fff3cd;
            color: #856404;
        }

        .status-badge.submitted {
            background: #d1ecf1;
            color: #0c5460;
        }

        .status-badge.graded {
            background: #d4edda;
            color: #155724;
        }

        .action-btn {
            padding: 6px 15px;
            background: #667eea;
            color: white;
            border: none;
            border-radius: 6px;
            cursor: pointer;
            font-size: 13px;
            transition: all 0.3s ease;
        }

        .action-btn:hover {
            background: #764ba2;
        }

        /* ============================================
           RESPONSIVE DESIGN - MEDIA QUERIES
           ============================================ */

        /* Large Tablets and Small Laptops (1024px and below) */
        @media (max-width: 1024px) {
            .content-grid {
                grid-template-columns: 1fr;
            }

            .stats-grid {
                grid-template-columns: repeat(2, 1fr);
            }

            /* Student Profile Section */
            .student-profile-grid {
                grid-template-columns: 1fr !important;
            }

            /* Notification Dropdown */
            .notification-dropdown {
