            }

            /* Student Profile Section - Mobile */
            div[style*="grid-template-columns: 280px 1fr"] {
                display: block !important;
            }

            div[style*="grid-template-columns: 280px 1fr"] > * {
                margin-bottom: 20px;
            }

            /* Student Info Cards */
            div[style*="grid-template-columns: repeat(auto-fit, minmax(200px, 1fr))"] {
                grid-template-columns: 1fr !important;
            }

            div[style*="grid-template-columns: repeat(2, 1fr)"] {
                grid-template-columns: 1fr !important;
            }

            div[style*="grid-template-columns: repeat(auto-fit, minmax(150px, 1fr))"] {
                grid-template-columns: repeat(2, 1fr) !important;
            }

            /* User Profile */
            .user-profile {
                padding: 8px 12px;
                gap: 8px;
            }

            .user-avatar {
                width: 40px;
                height: 40px;
                font-size: 16px;
            }

            .user-info h4 {
                font-size: 13px;
            }

            .user-info p {
                font-size: 11px;
            }

            /* Dropdowns */
            .notification-dropdown {
                position: fixed;
                top: auto;
                right: 10px;
                left: 10px;
                width: auto;
                max-height: 70vh;
                overflow-y: auto;
            }

            .profile-dropdown {
                position: fixed;
                top: auto;
                right: 10px;
                left: 10px;
                width: auto;
                min-width: auto;
                max-height: 80vh;
                overflow-y: auto;
            }

            .profile-stats {
                grid-template-columns: repeat(3, 1fr);
                gap: 6px;
            }

            .profile-stat {
                padding: 10px 8px;
            }

            .profile-stat-value {
                font-size: 16px;
            }

            .profile-stat-label {
                font-size: 10px;
            }

            /* Content Cards */
            .content-card {
                padding: 20px;
            }

            .card-header h2 {
                font-size: 18px;
            }

            /* Tables */
            .assignments-table,
            .courses-table {
                display: block;
                overflow-x: auto;
                white-space: nowrap;
            }

            .assignments-table th,
            .assignments-table td,
            .courses-table th,
            .courses-table td {
                padding: 10px;
                font-size: 13px;
            }

            /* Modals */
            .modal-content {
                width: 95%;
                max-width: 95%;
                margin: 20px auto;
                max-height: 90vh;
                overflow-y: auto;
            }

            .modal-header h2 {
                font-size: 20px;
            }

            /* Notification Items */
            .notification-item {
                padding: 12px;
            }

            .notification-icon {
                width: 40px;
                height: 40px;
            }

            .notification-title {
                font-size: 13px;
            }

            .notification-text {
                font-size: 12px;
            }

            /* Edit Submission Modal */
            .file-upload-area {
                padding: 30px;
            }

            .upload-icon {
                font-size: 40px;
            }

            /* Calendar Buttons */
            .calendar-buttons {
                flex-wrap: wrap;
            }

            .calendar-btn {
                flex: 1 1 calc(50% - 5px);
                min-width: auto;
            }

            /* Action Buttons */
            .action-buttons {
                flex-wrap: wrap;
                gap: 8px;
            }

            .action-btn {
                flex: 1;
                min-width: calc(50% - 4px);
                padding: 10px 16px;
                font-size: 13px;
            }

            /* Filters */
            .filter-buttons {
                flex-wrap: wrap;
                gap: 8px;
            }

            .filter-btn {
                padding: 8px 16px;
                font-size: 13px;
            }
        }

        /* Mobile Phones (480px and below) */
        @media (max-width: 480px) {
            /* Header */
            .header-left h1 {
                font-size: 20px;
            }

            .header-left p {
                font-size: 12px;
            }

            /* Stats Cards */
            .stat-card {
                padding: 20px;
            }

            .stat-icon {
                width: 50px;
                height: 50px;
                font-size: 24px;
            }

            .stat-content h3 {
                font-size: 28px;
            }

            .stat-content p {
                font-size: 13px;
            }

            /* Student Profile Section */
            div[style*="padding: 35px"] {
                padding: 20px !important;
            }

            div[style*="font-size: 24px"] h2 {
                font-size: 20px !important;
            }

            /* Profile Avatar */
            div[style*="width: 120px; height: 120px"] {
                width: 100px !important;
                height: 100px !important;
                font-size: 40px !important;
            }

            /* User Profile Dropdown */
            .user-info h4 {
                font-size: 12px;
            }

            .user-info p {
                display: none; 
            }

            .dropdown-arrow {
                display: none;
            }

            /* Profile Menu */
            .profile-menu-link {
                padding: 10px 14px;
                font-size: 13px;
            }

            /* Modal */
            .modal-content {
                width: 100%;
                height: 100vh;
                max-height: 100vh;
                margin: 0;
                border-radius: 0;
            }

            .modal-header {
                padding: 15px 20px;
            }

            .modal-body {
                padding: 15px;
            }

            .modal-footer {
                padding: 15px 20px;
            }

            /* Notification Modal */
            .notifications-header {
                padding: 15px;
            }

            .notifications-header h2 {
                font-size: 18px;
            }

            .search-filter-bar {
                flex-direction: column;
                gap: 10px;
            }

            .search-bar {
                width: 100%;
            }

            .filter-buttons {
                width: 100%;
                justify-content: flex-start;
            }

            /* Tables - Stack on very small screens */
            .assignments-table thead {
                display: none;
            }

            .assignments-table tr {
                display: block;
                margin-bottom: 15px;
                border: 1px solid #e0e0e0;
                border-radius: 8px;
                padding: 15px;
            }

            .assignments-table td {
                display: block;
                text-align: left !important;
                padding: 8px 0;
                border: none;
            }

            .assignments-table td:before {
                content: attr(data-label);
                font-weight: 600;
                display: inline-block;
                width: 120px;
                color: #7f8c8d;
            }

            /* Action Buttons - Full Width */
            .action-btn {
                width: 100%;
                flex: none;
            }

            .calendar-btn {
                width: 100%;
                flex: none;
            }

            /* Quick Actions */
            div[style*="grid-template-columns: repeat(auto-fit, minmax(150px, 1fr))"] button {
                font-size: 13px !important;
                padding: 12px !important;
            }

            /* File Upload */
            .file-upload-area {
                padding: 20px;
            }

            .upload-icon {
                font-size: 32px;
            }

            .upload-text {
                font-size: 14px;
            }

            /* Sidebar Navigation */
            .nav-link {
                padding: 10px 12px;
                font-size: 14px;
            }

            .nav-link i {
                font-size: 18px;
            }

            /* Content Cards */
            .content-card {
                padding: 15px;
                border-radius: 12px;
            }

            .card-header {
                flex-direction: column;
                gap: 12px;
                align-items: flex-start;
            }

            .view-all {
                align-self: flex-end;
            }

            /* All Courses Modal Responsive */
            .all-courses-modal-content {
                width: 100%;
                max-height: 100vh;
                border-radius: 0;
            }

            .all-courses-modal-header {
                padding: 20px;
            }

            .all-courses-modal-header h2 {
                font-size: 24px;
            }

            .all-courses-toolbar {
                padding: 15px 20px;
                flex-direction: column;
            }

            .courses-search-box {
                width: 100%;
            }

            .courses-filter-group {
                width: 100%;
                flex-direction: column;
                align-items: flex-start;
            }

            .filter-select {
                width: 100%;
            }

            .view-toggle {
                width: 100%;
                justify-content: center;
            }

            .all-courses-modal-body {
                padding: 20px;
            }

            .courses-stats-bar {
                grid-template-columns: repeat(2, 1fr);
            }

            .courses-grid {
                grid-template-columns: 1fr;
            }

            .course-card-banner {
                height: 150px;
            }

            .course-list-item {
                flex-direction: column;
                padding: 20px;
            }

            .course-list-thumbnail {
                width: 100%;
                height: 150px;
            }

            .course-list-progress-section {
                width: 100%;
            }
        }

        /* Extra Small Devices (360px and below) */
        @media (max-width: 360px) {
            .header-left h1 {
                font-size: 18px;
            }

            .stat-content h3 {
                font-size: 24px;
            }

            .profile-stats {
                grid-template-columns: 1fr;
            }

            .notification-wrapper,
            .user-profile-wrapper {
                position: static;
            }

            .profile-dropdown,
            .notification-dropdown {
                position: fixed;
                top: 60px;
                left: 5px;
                right: 5px;
                width: auto;
            }
        }

        /* Landscape Orientation for Mobile */
        @media (max-height: 600px) and (orientation: landscape) {
            .sidebar {
                overflow-y: auto;
            }

            .modal-content {
                max-height: 95vh;
                overflow-y: auto;
            }

            .profile-dropdown,
            .notification-dropdown {
                max-height: 85vh;
            }
        }

        /* Touch Device Optimizations */
        @media (hover: none) and (pointer: coarse) {
            .nav-link,
            .action-btn,
            .filter-btn,
            .profile-menu-link,
            .calendar-btn {
                min-height: 44px; /* Apple's recommended touch target size */
            }

            .user-profile {
                min-height: 48px;
            }

            .notification-btn {
                min-width: 44px;
                min-height: 44px;
            }
        }

    
        @keyframes fadeIn {
            from {
                opacity: 0;
                transform: translateY(20px);
            }
            to {
                opacity: 1;
                transform: translateY(0);
            }
        }

        .stat-card,
        .content-card {
            animation: fadeIn 0.5s ease-out;
        }
    </style>
</head>
