        function showNotificationMessage(message, type) {
            
            const existingToast = document.getElementById('notificationToast');
            if (existingToast) {
                existingToast.remove();
            }
            
            // Create toast element
            const toast = document.createElement('div');
            toast.id = 'notificationToast';
            toast.style.cssText = `
                position: fixed;
                bottom: 30px;
                right: 30px;
                background: ${type === 'success' ? '#43e97b' : type === 'info' ? '#4facfe' : '#fa709a'};
                color: white;
                padding: 16px 24px;
                border-radius: 12px;
                box-shadow: 0 8px 32px rgba(0,0,0,0.2);
                z-index: 100000;
                font-size: 14px;
                font-weight: 500;
                animation: slideInUp 0.3s ease-out;
                max-width: 400px;
            `;
            
            // Add animation styles if not already added
            if (!document.getElementById('toastAnimationStyles')) {
                const toastStyle = document.createElement('style');
                toastStyle.id = 'toastAnimationStyles';
                toastStyle.textContent = `
                    @keyframes slideInUp {
                        from {
                            transform: translateY(100px);
                            opacity: 0;
                        }
                        to {
                            transform: translateY(0);
                            opacity: 1;
                        }
                    }
                    @keyframes slideOutDown {
                        from {
                            transform: translateY(0);
                            opacity: 1;
                        }
                        to {
                            transform: translateY(100px);
                            opacity: 0;
                        }
                    }
                `;
                document.head.appendChild(toastStyle);
            }
            
            // Add icon based on type
            const icon = type === 'success' ? '' : type === 'info' ? '' : '!';
            toast.innerHTML = `<i class="fas fa-${type === 'success' ? 'check-circle' : type === 'info' ? 'info-circle' : 'exclamation-circle'}"></i> ${message}`;
            
            document.body.appendChild(toast);
            
            // Auto remove after 3 seconds
            setTimeout(function() {
                toast.style.animation = 'slideOutDown 0.3s ease-out';
                setTimeout(function() {
                    toast.remove();
                }, 300);
            }, 3000);
        }

